import React, { useState, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';
import DashboardCharts from './DashboardCharts';
import './Dashboard.css';

const Dashboard = () => {
  const navigate = useNavigate();
  const [currentUser, setCurrentUser] = useState(null);
  const [activities, setActivities] = useState([]);
  const [totalEmissions, setTotalEmissions] = useState(0);

  useEffect(() => {
    const user = JSON.parse(localStorage.getItem('currentUser') || 'null');
    if (!user) {
      navigate('/auth');
      return;
    }
    setCurrentUser(user);
    loadActivities();
  }, [navigate]);

  const loadActivities = () => {
    const user = JSON.parse(localStorage.getItem('currentUser') || 'null');
    if (user) {
      const saved = localStorage.getItem(`carbonActivities_${user.id}`);
      if (saved) {
        try {
          const parsed = JSON.parse(saved);
          setActivities(parsed);
          calculateTotal(parsed);
        } catch (error) {
          console.error('Error loading activities:', error);
        }
      }
    }
  };

  const calculateTotal = (activitiesList) => {
    const total = activitiesList.reduce((sum, activity) => {
      return sum + (activity.co2e || 0);
    }, 0);
    setTotalEmissions(total);
  };

  const handleLogout = () => {
    localStorage.removeItem('currentUser');
    navigate('/');
  };

  const getDailyEmissions = () => {
    const daily = {};
    activities.forEach(activity => {
      const date = activity.date;
      if (!daily[date]) {
        daily[date] = 0;
      }
      daily[date] += activity.co2e || 0;
    });
    return daily;
  };

  const getWeeklyEmissions = () => {
    const weekly = {};
    activities.forEach(activity => {
      const date = new Date(activity.date);
      const weekStart = new Date(date);
      weekStart.setDate(date.getDate() - date.getDay());
      const weekKey = weekStart.toISOString().split('T')[0];
      
      if (!weekly[weekKey]) {
        weekly[weekKey] = 0;
      }
      weekly[weekKey] += activity.co2e || 0;
    });
    return weekly;
  };

  const getEmissionsByType = () => {
    const byType = {};
    activities.forEach(activity => {
      const type = activity.type;
      if (!byType[type]) {
        byType[type] = 0;
      }
      byType[type] += activity.co2e || 0;
    });
    return byType;
  };

  if (!currentUser) {
    return null;
  }

  return (
    <div className="dashboard-page">
      <header className="dashboard-header">
        <div className="header-content">
          <div className="header-left">
            <h1>CarbonVision Dashboard</h1>
            <p>Welcome back, {currentUser.name}!</p>
          </div>
          <button className="logout-btn" onClick={handleLogout}>
            Logout
          </button>
        </div>
      </header>

      <div className="dashboard-container">
        <div className="total-emissions-card">
          <div className="emission-value">
            <span className="emission-number">{totalEmissions.toFixed(2)}</span>
            <span className="emission-unit">kg COâ‚‚e</span>
          </div>
          <p className="emission-description">
            {totalEmissions === 0 
              ? 'No activities recorded yet. Add activities during login to see your carbon footprint.'
              : totalEmissions < 10
              ? 'Great job! You\'re keeping your footprint low.'
              : totalEmissions < 50
              ? 'Consider reducing your daily emissions.'
              : 'Your footprint is high. Focus on reducing emissions.'}
          </p>
        </div>

        <div className="dashboard-content">
          <DashboardCharts
            dailyEmissions={getDailyEmissions()}
            weeklyEmissions={getWeeklyEmissions()}
            emissionsByType={getEmissionsByType()}
            activities={activities}
          />
        </div>
      </div>
    </div>
  );
};

export default Dashboard;
