import React, { useState } from 'react';
import './ActivityForm.css';

const ActivityForm = ({ type, onSubmit, loading }) => {
  const [formData, setFormData] = useState({
    distance: '',
    vehicleType: 'car',
    energy: '',
    foodType: 'vegetables',
    quantity: ''
  });

  const handleSubmit = (e) => {
    e.preventDefault();
    
    if (type === 'commute' && !formData.distance) {
      alert('Please enter distance');
      return;
    }
    if (type === 'electricity' && !formData.energy) {
      alert('Please enter energy consumption');
      return;
    }
    if (type === 'food' && !formData.quantity) {
      alert('Please enter quantity');
      return;
    }

    onSubmit({
      type,
      ...formData
    });

    // Reset form
    setFormData({
      distance: '',
      vehicleType: 'car',
      energy: '',
      foodType: 'vegetables',
      quantity: ''
    });
  };

  const handleChange = (e) => {
    setFormData({
      ...formData,
      [e.target.name]: e.target.value
    });
  };

  return (
    <div className="activity-form-container">
      <form className="activity-form" onSubmit={handleSubmit}>
        {type === 'commute' && (
          <>
            <div className="form-group">
              <label htmlFor="vehicleType">Vehicle Type</label>
              <select
                id="vehicleType"
                name="vehicleType"
                value={formData.vehicleType}
                onChange={handleChange}
                className="form-input"
              >
                <option value="car">Car</option>
                <option value="bus">Bus</option>
                <option value="train">Train</option>
                <option value="plane">Plane</option>
                <option value="motorcycle">Motorcycle</option>
              </select>
            </div>
            <div className="form-group">
              <label htmlFor="distance">Distance (km)</label>
              <input
                type="number"
                id="distance"
                name="distance"
                value={formData.distance}
                onChange={handleChange}
                className="form-input"
                placeholder="Enter distance in kilometers"
                step="0.1"
                min="0"
                required
              />
            </div>
          </>
        )}

        {type === 'electricity' && (
          <div className="form-group">
            <label htmlFor="energy">Energy Consumption (kWh)</label>
            <input
              type="number"
              id="energy"
              name="energy"
              value={formData.energy}
              onChange={handleChange}
              className="form-input"
              placeholder="Enter energy consumption in kWh"
              step="0.1"
              min="0"
              required
            />
          </div>
        )}

        {type === 'food' && (
          <>
            <div className="form-group">
              <label htmlFor="foodType">Food Type</label>
              <select
                id="foodType"
                name="foodType"
                value={formData.foodType}
                onChange={handleChange}
                className="form-input"
              >
                <option value="beef">Beef</option>
                <option value="chicken">Chicken</option>
                <option value="pork">Pork</option>
                <option value="fish">Fish</option>
                <option value="dairy">Dairy</option>
                <option value="vegetables">Vegetables</option>
                <option value="fruits">Fruits</option>
              </select>
            </div>
            <div className="form-group">
              <label htmlFor="quantity">Quantity (kg)</label>
              <input
                type="number"
                id="quantity"
                name="quantity"
                value={formData.quantity}
                onChange={handleChange}
                className="form-input"
                placeholder="Enter quantity in kilograms"
                step="0.1"
                min="0"
                required
              />
            </div>
          </>
        )}

        <button type="submit" className="submit-button" disabled={loading}>
          {loading ? 'Calculating...' : 'Calculate Emissions'}
        </button>
      </form>
    </div>
  );
};

export default ActivityForm;
