import React from 'react';
import { Chart as ChartJS, CategoryScale, LinearScale, PointElement, LineElement, BarElement, Title, Tooltip, Legend, ArcElement } from 'chart.js';
import { Line, Bar, Pie } from 'react-chartjs-2';
import './DashboardCharts.css';

ChartJS.register(CategoryScale, LinearScale, PointElement, LineElement, BarElement, Title, Tooltip, Legend, ArcElement);

const DashboardCharts = ({ dailyEmissions, weeklyEmissions, emissionsByType, activities }) => {
  const dailyLabels = Object.keys(dailyEmissions).sort();
  const dailyData = dailyLabels.map(date => dailyEmissions[date]);
  const weeklyLabels = Object.keys(weeklyEmissions).sort();
  const weeklyData = weeklyLabels.map(week => weeklyEmissions[week]);
  const typeLabels = Object.keys(emissionsByType);
  const typeData = typeLabels.map(type => emissionsByType[type]);
  const typeColors = { 'commute': '#667eea', 'electricity': '#764ba2', 'food': '#f093fb' };

  const baseOptions = {
    responsive: true,
    maintainAspectRatio: false,
    plugins: {
      legend: { display: true, position: 'top', labels: { font: { size: 14, family: 'Poppins' }, color: '#333', padding: 15 } },
      tooltip: { backgroundColor: 'rgba(0, 0, 0, 0.8)', padding: 12, titleFont: { size: 14, family: 'Poppins' }, bodyFont: { size: 13, family: 'Poppins' } }
    }
  };

  const lineBarOptions = {
    ...baseOptions,
    scales: {
      y: { beginAtZero: true, ticks: { font: { size: 12, family: 'Poppins' }, color: '#666' }, grid: { color: 'rgba(0, 0, 0, 0.05)' } },
      x: { ticks: { font: { size: 12, family: 'Poppins' }, color: '#666' }, grid: { display: false } }
    }
  };

  const dailyChartData = {
    labels: dailyLabels.map(date => {
      const d = new Date(date + 'T00:00:00');
      return d.toLocaleDateString('en-US', { month: 'short', day: 'numeric' });
    }),
    datasets: [{
      label: 'Daily CO‚ÇÇe Emissions (kg)',
      data: dailyData,
      borderColor: '#667eea',
      backgroundColor: 'rgba(102, 126, 234, 0.1)',
      borderWidth: 3,
      fill: true,
      tension: 0.4,
      pointRadius: 6,
      pointHoverRadius: 8
    }]
  };

  const weeklyChartData = {
    labels: weeklyLabels.map(week => {
      const d = new Date(week + 'T00:00:00');
      return `Week of ${d.toLocaleDateString('en-US', { month: 'short', day: 'numeric' })}`;
    }),
    datasets: [{
      label: 'Weekly CO‚ÇÇe Emissions (kg)',
      data: weeklyData,
      backgroundColor: 'rgba(102, 126, 234, 0.6)',
      borderColor: '#667eea',
      borderWidth: 2
    }]
  };

  const pieChartData = {
    labels: typeLabels.map(type => type.charAt(0).toUpperCase() + type.slice(1)),
    datasets: [{
      data: typeData,
      backgroundColor: typeLabels.map(type => typeColors[type] || '#999'),
      borderColor: '#fff',
      borderWidth: 3
    }]
  };

  const pieOptions = {
    ...baseOptions,
    plugins: {
      ...baseOptions.plugins,
      legend: { ...baseOptions.plugins.legend, position: 'right' },
      tooltip: {
        ...baseOptions.plugins.tooltip,
        callbacks: {
          label: (context) => {
            const total = context.dataset.data.reduce((a, b) => a + b, 0);
            const percentage = ((context.parsed / total) * 100).toFixed(1);
            return `${context.label}: ${context.parsed.toFixed(2)} kg (${percentage}%)`;
          }
        }
      }
    }
  };

  const hasData = dailyLabels.length > 0 || weeklyLabels.length > 0 || typeLabels.length > 0;

  if (!hasData) {
    return (
      <div className="charts-container">
        <div className="no-chart-data">
          <p>No emission data available yet.</p>
          <p>Start adding activities to see your carbon footprint visualized!</p>
          <p style={{ marginTop: '1rem', color: '#667eea', fontWeight: 600 }}>Switch to "Add Activity" tab to input your data.</p>
        </div>
      </div>
    );
  }

  const getActivityInfo = (activity) => {
    if (activity.type === 'commute') return { icon: 'üöó', title: `${activity.vehicleType} - ${activity.distance} km` };
    if (activity.type === 'electricity') return { icon: '‚ö°', title: `${activity.energy} kWh` };
    return { icon: 'üçΩÔ∏è', title: `${activity.foodType} - ${activity.quantity} kg` };
  };

  return (
    <div className="charts-container">
      <div className="charts-grid">
        <div className="chart-card">
          <h3>Daily Emissions Trend</h3>
          <div className="chart-wrapper">
            <Line data={dailyChartData} options={lineBarOptions} />
          </div>
        </div>
        <div className="chart-card">
          <h3>Weekly Emissions Summary</h3>
          <div className="chart-wrapper">
            <Bar data={weeklyChartData} options={lineBarOptions} />
          </div>
        </div>
        <div className="chart-card full-width">
          <h3>Emissions by Activity Type</h3>
          <div className="chart-wrapper pie-chart">
            <Pie data={pieChartData} options={pieOptions} />
          </div>
        </div>
      </div>
      <div className="activities-summary">
        <h3>Recent Activities</h3>
        <div className="activities-list">
          {activities.slice().reverse().slice(0, 10).map(activity => {
            const info = getActivityInfo(activity);
            return (
              <div key={activity.id} className="activity-summary-item">
                <div className="activity-icon">{info.icon}</div>
                <div className="activity-details">
                  <div className="activity-title">{info.title}</div>
                  <div className="activity-date">{activity.date}</div>
                </div>
                <div className="activity-emission">{activity.co2e?.toFixed(2)} kg CO‚ÇÇe</div>
              </div>
            );
          })}
        </div>
      </div>
    </div>
  );
};

export default DashboardCharts;
