import React from 'react';
import {
  Chart as ChartJS,
  CategoryScale,
  LinearScale,
  PointElement,
  LineElement,
  BarElement,
  Title,
  Tooltip,
  Legend,
  ArcElement
} from 'chart.js';
import { Line, Bar } from 'react-chartjs-2';
import './EmissionsChart.css';

ChartJS.register(
  CategoryScale,
  LinearScale,
  PointElement,
  LineElement,
  BarElement,
  Title,
  Tooltip,
  Legend,
  ArcElement
);

const EmissionsChart = ({ dailyEmissions, weeklyEmissions }) => {
  const dailyLabels = Object.keys(dailyEmissions).sort();
  const dailyData = dailyLabels.map(date => dailyEmissions[date]);

  const weeklyLabels = Object.keys(weeklyEmissions).sort();
  const weeklyData = weeklyLabels.map(week => weeklyEmissions[week]);

  const dailyChartData = {
    labels: dailyLabels.map(date => {
      const d = new Date(date);
      return d.toLocaleDateString('en-US', { month: 'short', day: 'numeric' });
    }),
    datasets: [
      {
        label: 'Daily CO₂e Emissions (kg)',
        data: dailyData,
        borderColor: '#2d8659',
        backgroundColor: 'rgba(45, 134, 89, 0.1)',
        borderWidth: 3,
        fill: true,
        tension: 0.4
      }
    ]
  };

  const weeklyChartData = {
    labels: weeklyLabels.map(week => {
      const d = new Date(week);
      return `Week of ${d.toLocaleDateString('en-US', { month: 'short', day: 'numeric' })}`;
    }),
    datasets: [
      {
        label: 'Weekly CO₂e Emissions (kg)',
        data: weeklyData,
        backgroundColor: 'rgba(45, 134, 89, 0.6)',
        borderColor: '#2d8659',
        borderWidth: 2
      }
    ]
  };

  const chartOptions = {
    responsive: true,
    maintainAspectRatio: false,
    plugins: {
      legend: {
        display: true,
        position: 'top',
        labels: {
          font: {
            size: 14,
            family: 'Poppins'
          },
          color: '#333'
        }
      },
      tooltip: {
        backgroundColor: 'rgba(0, 0, 0, 0.8)',
        padding: 12,
        titleFont: {
          size: 14,
          family: 'Poppins'
        },
        bodyFont: {
          size: 13,
          family: 'Poppins'
        }
      }
    },
    scales: {
      y: {
        beginAtZero: true,
        ticks: {
          font: {
            size: 12,
            family: 'Poppins'
          },
          color: '#666'
        },
        grid: {
          color: 'rgba(0, 0, 0, 0.05)'
        }
      },
      x: {
        ticks: {
          font: {
            size: 12,
            family: 'Poppins'
          },
          color: '#666'
        },
        grid: {
          display: false
        }
      }
    }
  };

  const hasData = dailyLabels.length > 0 || weeklyLabels.length > 0;

  if (!hasData) {
    return (
      <div className="chart-container">
        <div className="no-chart-data">
          <p>No emission data available yet.</p>
          <p>Start adding activities to see your carbon footprint visualized!</p>
        </div>
      </div>
    );
  }

  return (
    <div className="chart-container">
      <div className="chart-section">
        <h3>Daily Emissions Trend</h3>
        <div className="chart-wrapper">
          <Line data={dailyChartData} options={chartOptions} />
        </div>
      </div>

      <div className="chart-section">
        <h3>Weekly Emissions Summary</h3>
        <div className="chart-wrapper">
          <Bar data={weeklyChartData} options={chartOptions} />
        </div>
      </div>
    </div>
  );
};

export default EmissionsChart;
