import React, { useState, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';
import axios from 'axios';
import ActivityForm from './ActivityForm';
import DashboardCharts from './DashboardCharts';
import './Dashboard.css';

const API_BASE_URL = process.env.REACT_APP_API_URL || 'http://localhost:5000/api';

const Dashboard = () => {
  const navigate = useNavigate();
  const [currentUser, setCurrentUser] = useState(null);
  const [activities, setActivities] = useState([]);
  const [totalEmissions, setTotalEmissions] = useState(0);
  const [loading, setLoading] = useState(false);
  const [activeTab, setActiveTab] = useState('input');

  useEffect(() => {
    const user = JSON.parse(localStorage.getItem('currentUser') || 'null');
    if (!user) {
      navigate('/auth');
      return;
    }
    setCurrentUser(user);
    loadActivities();
  }, [navigate]);

  const loadActivities = () => {
    const user = JSON.parse(localStorage.getItem('currentUser') || 'null');
    if (user) {
      const saved = localStorage.getItem(`carbonActivities_${user.id}`);
      if (saved) {
        try {
          const parsed = JSON.parse(saved);
          setActivities(parsed);
          calculateTotal(parsed);
        } catch (error) {
          console.error('Error loading activities:', error);
        }
      }
    }
  };

  const calculateTotal = (activitiesList) => {
    const total = activitiesList.reduce((sum, activity) => {
      return sum + (activity.co2e || 0);
    }, 0);
    setTotalEmissions(total);
  };

  const handleActivitySubmit = async (activityData) => {
    setLoading(true);
    try {
      let response;
      
      switch (activityData.type) {
        case 'commute':
          response = await axios.post(`${API_BASE_URL}/calculate/commute`, {
            distance: parseFloat(activityData.distance),
            vehicleType: activityData.vehicleType,
            unit: 'km'
          });
          break;
        case 'electricity':
          response = await axios.post(`${API_BASE_URL}/calculate/electricity`, {
            energy: parseFloat(activityData.energy),
            unit: 'kWh'
          });
          break;
        case 'food':
          response = await axios.post(`${API_BASE_URL}/calculate/food`, {
            foodType: activityData.foodType,
            quantity: parseFloat(activityData.quantity),
            unit: 'kg'
          });
          break;
        default:
          throw new Error('Invalid activity type');
      }

      if (response.data.success) {
        const newActivity = {
          id: Date.now(),
          type: activityData.type,
          date: new Date().toISOString().split('T')[0],
          ...response.data,
          ...activityData
        };

        const user = JSON.parse(localStorage.getItem('currentUser') || 'null');
        const updatedActivities = [...activities, newActivity];
        setActivities(updatedActivities);
        calculateTotal(updatedActivities);
        localStorage.setItem(`carbonActivities_${user.id}`, JSON.stringify(updatedActivities));
      }
    } catch (error) {
      console.error('Error calculating emissions:', error);
      alert('Error calculating emissions. Please try again.');
    } finally {
      setLoading(false);
    }
  };

  const handleLogout = () => {
    localStorage.removeItem('currentUser');
    navigate('/');
  };

  const getDailyEmissions = () => {
    const daily = {};
    activities.forEach(activity => {
      const date = activity.date;
      if (!daily[date]) {
        daily[date] = 0;
      }
      daily[date] += activity.co2e || 0;
    });
    return daily;
  };

  const getWeeklyEmissions = () => {
    const weekly = {};
    activities.forEach(activity => {
      const date = new Date(activity.date);
      const weekStart = new Date(date);
      weekStart.setDate(date.getDate() - date.getDay());
      const weekKey = weekStart.toISOString().split('T')[0];
      
      if (!weekly[weekKey]) {
        weekly[weekKey] = 0;
      }
      weekly[weekKey] += activity.co2e || 0;
    });
    return weekly;
  };

  const getEmissionsByType = () => {
    const byType = {};
    activities.forEach(activity => {
      const type = activity.type;
      if (!byType[type]) {
        byType[type] = 0;
      }
      byType[type] += activity.co2e || 0;
    });
    return byType;
  };

  if (!currentUser) {
    return null;
  }

  return (
    <div className="dashboard-page">
      <header className="dashboard-header">
        <div className="header-content">
          <div className="header-left">
            <h1>CarbonVision Dashboard</h1>
            <p>Welcome back, {currentUser.name}!</p>
          </div>
          <button className="logout-btn" onClick={handleLogout}>
            Logout
          </button>
        </div>
      </header>

      <div className="dashboard-container">
        <div className="total-emissions-card">
          <div className="emission-value">
            <span className="emission-number">{totalEmissions.toFixed(2)}</span>
            <span className="emission-unit">kg COâ‚‚e</span>
          </div>
          <p className="emission-description">
            {totalEmissions === 0 
              ? 'Start tracking your activities to see your carbon footprint'
              : totalEmissions < 10
              ? 'Great job! You\'re keeping your footprint low.'
              : totalEmissions < 50
              ? 'Consider reducing your daily emissions.'
              : 'Your footprint is high. Focus on reducing emissions.'}
          </p>
        </div>

        <div className="dashboard-tabs">
          <button 
            className={`tab ${activeTab === 'input' ? 'active' : ''}`}
            onClick={() => setActiveTab('input')}
          >
            âž• Add Activity
          </button>
          <button 
            className={`tab ${activeTab === 'dashboard' ? 'active' : ''}`}
            onClick={() => setActiveTab('dashboard')}
          >
            ðŸ“Š Dashboard
          </button>
        </div>

        <div className="tab-content">
          {activeTab === 'input' && (
            <div className="input-section">
              <ActivityForm 
                type="commute"
                onSubmit={(data) => handleActivitySubmit({ ...data, type: 'commute' })}
                loading={loading}
              />
              <ActivityForm 
                type="electricity"
                onSubmit={(data) => handleActivitySubmit({ ...data, type: 'electricity' })}
                loading={loading}
              />
              <ActivityForm 
                type="food"
                onSubmit={(data) => handleActivitySubmit({ ...data, type: 'food' })}
                loading={loading}
              />
            </div>
          )}

          {activeTab === 'dashboard' && (
            <DashboardCharts
              dailyEmissions={getDailyEmissions()}
              weeklyEmissions={getWeeklyEmissions()}
              emissionsByType={getEmissionsByType()}
              activities={activities}
            />
          )}
        </div>
      </div>
    </div>
  );
};

export default Dashboard;
