import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';
import axios from 'axios';
import ActivityForm from './ActivityForm';
import './Auth.css';

const API_BASE_URL = process.env.REACT_APP_API_URL || 'http://localhost:5000/api';

const Auth = () => {
  const navigate = useNavigate();
  const [isLogin, setIsLogin] = useState(true);
  const [showActivityForm, setShowActivityForm] = useState(false);
  const [currentUser, setCurrentUser] = useState(null);
  const [formData, setFormData] = useState({
    name: '',
    email: '',
    password: ''
  });
  const [error, setError] = useState('');
  const [loading, setLoading] = useState(false);
  const [activities, setActivities] = useState([]);

  const handleChange = (e) => {
    setFormData({
      ...formData,
      [e.target.name]: e.target.value
    });
    setError('');
  };

  const handleAuthSubmit = async (e) => {
    e.preventDefault();
    
    if (isLogin) {
      // Login
      const users = JSON.parse(localStorage.getItem('users') || '[]');
      const user = users.find(u => u.email === formData.email && u.password === formData.password);
      
      if (user) {
        setCurrentUser(user);
        localStorage.setItem('currentUser', JSON.stringify(user));
        // Check if user has activities
        const saved = localStorage.getItem(`carbonActivities_${user.id}`);
        if (saved) {
          // User has data, go to dashboard
          navigate('/dashboard');
        } else {
          // No data, show activity form
          setShowActivityForm(true);
        }
      } else {
        setError('Invalid email or password');
      }
    } else {
      // Signup
      if (!formData.name || !formData.email || !formData.password) {
        setError('All fields are required');
        return;
      }
      
      const users = JSON.parse(localStorage.getItem('users') || '[]');
      if (users.find(u => u.email === formData.email)) {
        setError('Email already exists');
        return;
      }
      
      const newUser = {
        id: Date.now(),
        name: formData.name,
        email: formData.email,
        password: formData.password
      };
      
      users.push(newUser);
      localStorage.setItem('users', JSON.stringify(users));
      setCurrentUser(newUser);
      localStorage.setItem('currentUser', JSON.stringify(newUser));
      setShowActivityForm(true);
    }
  };

  const handleActivitySubmit = async (activityData) => {
    setLoading(true);
    try {
      let response;
      
      switch (activityData.type) {
        case 'commute':
          response = await axios.post(`${API_BASE_URL}/calculate/commute`, {
            distance: parseFloat(activityData.distance),
            vehicleType: activityData.vehicleType,
            unit: 'km'
          });
          break;
        case 'electricity':
          response = await axios.post(`${API_BASE_URL}/calculate/electricity`, {
            energy: parseFloat(activityData.energy),
            unit: 'kWh'
          });
          break;
        case 'food':
          response = await axios.post(`${API_BASE_URL}/calculate/food`, {
            foodType: activityData.foodType,
            quantity: parseFloat(activityData.quantity),
            unit: 'kg'
          });
          break;
        default:
          throw new Error('Invalid activity type');
      }

      if (response.data.success) {
        const newActivity = {
          id: Date.now(),
          type: activityData.type,
          date: new Date().toISOString().split('T')[0],
          ...response.data,
          ...activityData
        };

        const updatedActivities = [...activities, newActivity];
        setActivities(updatedActivities);
      }
    } catch (error) {
      console.error('Error calculating emissions:', error);
      alert('Error calculating emissions. Please try again.');
    } finally {
      setLoading(false);
    }
  };

  const handleContinueToDashboard = () => {
    if (currentUser && activities.length > 0) {
      localStorage.setItem(`carbonActivities_${currentUser.id}`, JSON.stringify(activities));
    }
    navigate('/dashboard');
  };

  if (showActivityForm) {
    return (
      <div className="auth-page">
        <div className="auth-container activity-form-container">
          <div className="auth-card">
            <div className="auth-header">
              <h1>Welcome, {currentUser?.name}!</h1>
              <p>Let's track your carbon footprint</p>
            </div>

            <div className="activity-forms-section">
              <ActivityForm 
                type="commute"
                onSubmit={(data) => handleActivitySubmit({ ...data, type: 'commute' })}
                loading={loading}
              />
              <ActivityForm 
                type="electricity"
                onSubmit={(data) => handleActivitySubmit({ ...data, type: 'electricity' })}
                loading={loading}
              />
              <ActivityForm 
                type="food"
                onSubmit={(data) => handleActivitySubmit({ ...data, type: 'food' })}
                loading={loading}
              />
            </div>

            <div className="form-actions">
              <button 
                className="skip-btn" 
                onClick={handleContinueToDashboard}
              >
                Skip for now
              </button>
              <button 
                className="continue-btn" 
                onClick={handleContinueToDashboard}
                disabled={activities.length === 0}
              >
                Continue to Dashboard →
              </button>
            </div>
          </div>
        </div>
      </div>
    );
  }

  return (
    <div className="auth-page">
      <div className="auth-container">
        <div className="auth-card">
          <div className="auth-header">
            <h1>CarbonVision</h1>
            <p>{isLogin ? 'Welcome back!' : 'Join the movement'}</p>
          </div>

          <div className="auth-tabs">
            <button 
              className={isLogin ? 'active' : ''}
              onClick={() => {
                setIsLogin(true);
                setError('');
                setFormData({ name: '', email: '', password: '' });
              }}
            >
              Login
            </button>
            <button 
              className={!isLogin ? 'active' : ''}
              onClick={() => {
                setIsLogin(false);
                setError('');
                setFormData({ name: '', email: '', password: '' });
              }}
            >
              Sign Up
            </button>
          </div>

          <form onSubmit={handleAuthSubmit} className="auth-form">
            {!isLogin && (
              <div className="form-group">
                <label>Name</label>
                <input
                  type="text"
                  name="name"
                  value={formData.name}
                  onChange={handleChange}
                  placeholder="Enter your name"
                  required
                />
              </div>
            )}

            <div className="form-group">
              <label>Email</label>
              <input
                type="email"
                name="email"
                value={formData.email}
                onChange={handleChange}
                placeholder="Enter your email"
                required
              />
            </div>

            <div className="form-group">
              <label>Password</label>
              <input
                type="password"
                name="password"
                value={formData.password}
                onChange={handleChange}
                placeholder="Enter your password"
                required
              />
            </div>

            {error && <div className="error-message">{error}</div>}

            <button type="submit" className="auth-submit-btn">
              {isLogin ? 'Login' : 'Sign Up'}
            </button>
          </form>

          <button className="back-to-home" onClick={() => navigate('/')}>
            ← Back to Home
          </button>
        </div>
      </div>
    </div>
  );
};

export default Auth;
