import React, { useState, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';
import axios from 'axios';
import ActivityForm from './ActivityForm';
import DashboardCharts from './DashboardCharts';
import './Dashboard.css';

const API_BASE_URL = process.env.REACT_APP_API_URL || 'http://localhost:5000/api';

const Dashboard = () => {
  const navigate = useNavigate();
  const [currentUser, setCurrentUser] = useState(null);
  const [activities, setActivities] = useState([]);
  const [totalEmissions, setTotalEmissions] = useState(0);
  const [loading, setLoading] = useState(false);
  const [activeTab, setActiveTab] = useState('dashboard');
  const [activityData, setActivityData] = useState({
    commute: { vehicleType: 'car', distance: '' },
    electricity: { energy: '' },
    food: { foodType: 'vegetables', quantity: '' }
  });

  useEffect(() => {
    const user = JSON.parse(localStorage.getItem('currentUser') || 'null');
    if (!user) {
      navigate('/auth');
      return;
    }
    setCurrentUser(user);
    loadActivities();
  }, [navigate]);

  const loadActivities = () => {
    const user = JSON.parse(localStorage.getItem('currentUser') || 'null');
    if (user) {
      const saved = localStorage.getItem(`carbonActivities_${user.id}`);
      if (saved) {
        try {
          const parsed = JSON.parse(saved);
          setActivities(parsed);
          calculateTotal(parsed);
        } catch (error) {
          console.error('Error loading activities:', error);
        }
      }
    }
  };

  const calculateTotal = (activitiesList) => {
    const total = activitiesList.reduce((sum, activity) => {
      return sum + (activity.co2e || 0);
    }, 0);
    setTotalEmissions(total);
  };

  const handleActivityChange = (type, name, value) => {
    setActivityData(prev => ({
      ...prev,
      [type]: {
        ...prev[type],
        [name]: value
      }
    }));
  };

  const handleSubmitAllActivities = async () => {
    setLoading(true);
    const newActivities = [];

    try {
      // Process commute
      if (activityData.commute.distance) {
        const response = await axios.post(`${API_BASE_URL}/calculate/commute`, {
          distance: parseFloat(activityData.commute.distance),
          vehicleType: activityData.commute.vehicleType,
          unit: 'km'
        });
        if (response.data.success) {
          newActivities.push({
            id: Date.now(),
            type: 'commute',
            date: new Date().toISOString().split('T')[0],
            ...response.data,
            ...activityData.commute
          });
        }
      }

      // Process electricity
      if (activityData.electricity.energy) {
        const response = await axios.post(`${API_BASE_URL}/calculate/electricity`, {
          energy: parseFloat(activityData.electricity.energy),
          unit: 'kWh'
        });
        if (response.data.success) {
          newActivities.push({
            id: Date.now() + 1,
            type: 'electricity',
            date: new Date().toISOString().split('T')[0],
            ...response.data,
            ...activityData.electricity
          });
        }
      }

      // Process food
      if (activityData.food.quantity) {
        const response = await axios.post(`${API_BASE_URL}/calculate/food`, {
          foodType: activityData.food.foodType,
          quantity: parseFloat(activityData.food.quantity),
          unit: 'kg'
        });
        if (response.data.success) {
          newActivities.push({
            id: Date.now() + 2,
            type: 'food',
            date: new Date().toISOString().split('T')[0],
            ...response.data,
            ...activityData.food
          });
        }
      }

      if (newActivities.length > 0 && currentUser) {
        const updatedActivities = [...activities, ...newActivities];
        setActivities(updatedActivities);
        calculateTotal(updatedActivities);
        localStorage.setItem(`carbonActivities_${currentUser.id}`, JSON.stringify(updatedActivities));
        
        // Reset form
        setActivityData({
          commute: { vehicleType: 'car', distance: '' },
          electricity: { energy: '' },
          food: { foodType: 'vegetables', quantity: '' }
        });
        
        // Switch to dashboard tab to see updated charts
        setActiveTab('dashboard');
      }
    } catch (error) {
      console.error('Error calculating emissions:', error);
      alert('Error calculating emissions. Please try again.');
    } finally {
      setLoading(false);
    }
  };

  const handleLogout = () => {
    localStorage.removeItem('currentUser');
    navigate('/');
  };

  const getDailyEmissions = () => {
    const daily = {};
    activities.forEach(activity => {
      const date = activity.date;
      if (!daily[date]) {
        daily[date] = 0;
      }
      daily[date] += activity.co2e || 0;
    });
    return daily;
  };

  const getWeeklyEmissions = () => {
    const weekly = {};
    activities.forEach(activity => {
      const date = new Date(activity.date);
      const weekStart = new Date(date);
      weekStart.setDate(date.getDate() - date.getDay());
      const weekKey = weekStart.toISOString().split('T')[0];
      
      if (!weekly[weekKey]) {
        weekly[weekKey] = 0;
      }
      weekly[weekKey] += activity.co2e || 0;
    });
    return weekly;
  };

  const getEmissionsByType = () => {
    const byType = {};
    activities.forEach(activity => {
      const type = activity.type;
      if (!byType[type]) {
        byType[type] = 0;
      }
      byType[type] += activity.co2e || 0;
    });
    return byType;
  };

  if (!currentUser) {
    return null;
  }

  return (
    <div className="dashboard-page">
      <header className="dashboard-header">
        <div className="header-content">
          <div className="header-left">
            <h1>CarbonVision Dashboard</h1>
            <p>Welcome back, {currentUser.name}!</p>
          </div>
          <button className="logout-btn" onClick={handleLogout}>
            Logout
          </button>
        </div>
      </header>

      <div className="dashboard-container">
        <div className="total-emissions-card">
          <div className="emission-value">
            <span className="emission-number">{totalEmissions.toFixed(2)}</span>
            <span className="emission-unit">kg COâ‚‚e</span>
          </div>
          <p className="emission-description">
            {totalEmissions === 0 
              ? 'No activities recorded yet. Add activities to see your carbon footprint.'
              : totalEmissions < 10
              ? 'Great job! You\'re keeping your footprint low.'
              : totalEmissions < 50
              ? 'Consider reducing your daily emissions.'
              : 'Your footprint is high. Focus on reducing emissions.'}
          </p>
        </div>

        <div className="dashboard-tabs">
          <button 
            className={`tab ${activeTab === 'dashboard' ? 'active' : ''}`}
            onClick={() => setActiveTab('dashboard')}
          >
            ðŸ“Š Dashboard
          </button>
          <button 
            className={`tab ${activeTab === 'add' ? 'active' : ''}`}
            onClick={() => setActiveTab('add')}
          >
            âž• Add Activity
          </button>
        </div>

        <div className="dashboard-content">
          {activeTab === 'dashboard' && (
            <DashboardCharts
              dailyEmissions={getDailyEmissions()}
              weeklyEmissions={getWeeklyEmissions()}
              emissionsByType={getEmissionsByType()}
              activities={activities}
            />
          )}

          {activeTab === 'add' && (
            <div className="add-activity-section">
              <h2 className="section-title">Add New Activities</h2>
              <div className="activity-forms-section">
                <ActivityForm 
                  type="commute"
                  formData={activityData.commute}
                  onChange={handleActivityChange}
                />
                <ActivityForm 
                  type="electricity"
                  formData={activityData.electricity}
                  onChange={handleActivityChange}
                />
                <ActivityForm 
                  type="food"
                  formData={activityData.food}
                  onChange={handleActivityChange}
                />
              </div>
              <div className="form-actions">
                <button 
                  className="submit-all-btn" 
                  onClick={handleSubmitAllActivities}
                  disabled={loading || (!activityData.commute.distance && !activityData.electricity.energy && !activityData.food.quantity)}
                >
                  {loading ? 'Calculating...' : 'Calculate & Add to Dashboard â†’'}
                </button>
              </div>
            </div>
          )}
        </div>
      </div>
    </div>
  );
};

export default Dashboard;
