import React, { useState, useEffect } from 'react';
import axios from 'axios';
import './OffsetSuggestions.css';

const API_BASE_URL = process.env.REACT_APP_API_URL || 'http://localhost:5000/api';

const OffsetSuggestions = ({ totalEmissions }) => {
  const [projects, setProjects] = useState([]);
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    fetchOffsetProjects();
  }, []);

  const fetchOffsetProjects = async () => {
    try {
      const response = await axios.get(`${API_BASE_URL}/offsets`);
      if (response.data.success) {
        setProjects(response.data.projects || []);
      }
    } catch (error) {
      console.error('Error fetching offset projects:', error);
      // Use fallback projects if API fails
      setProjects([
        {
          id: 1,
          name: 'Renewable Energy Project',
          description: 'Support clean energy initiatives that reduce reliance on fossil fuels',
          co2e_per_dollar: 0.5,
          location: 'Global'
        },
        {
          id: 2,
          name: 'Reforestation Program',
          description: 'Plant trees to absorb COâ‚‚ and restore natural ecosystems',
          co2e_per_dollar: 0.3,
          location: 'Global'
        },
        {
          id: 3,
          name: 'Clean Water Access',
          description: 'Reduce emissions through clean water infrastructure projects',
          co2e_per_dollar: 0.4,
          location: 'Global'
        }
      ]);
    } finally {
      setLoading(false);
    }
  };

  const calculateOffsetCost = (co2e, co2ePerDollar = 0.5) => {
    return (co2e / co2ePerDollar).toFixed(2);
  };

  if (loading) {
    return (
      <div className="offset-container">
        <div className="loading">Loading offset suggestions...</div>
      </div>
    );
  }

  return (
    <div className="offset-container">
      <div className="offset-header">
        <h2>Carbon Offset Suggestions</h2>
        <p className="offset-description">
          Offset your carbon footprint by supporting verified environmental projects.
          Your current footprint: <strong>{totalEmissions.toFixed(2)} kg COâ‚‚e</strong>
        </p>
      </div>

      {totalEmissions === 0 ? (
        <div className="no-offset-needed">
          <p>Great! You have no emissions to offset yet.</p>
          <p>Start tracking your activities to see offset recommendations.</p>
        </div>
      ) : (
        <div className="offset-projects">
          {projects.length === 0 ? (
            <div className="no-projects">
              <p>No offset projects available at the moment.</p>
            </div>
          ) : (
            projects.map((project, index) => {
              const co2ePerDollar = project.co2e_per_dollar || 0.5;
              const estimatedCost = calculateOffsetCost(totalEmissions, co2ePerDollar);
              
              return (
                <div key={project.id || index} className="offset-project-card">
                  <div className="project-icon">ðŸŒ±</div>
                  <div className="project-content">
                    <h3>{project.name || 'Environmental Project'}</h3>
                    <p className="project-description">
                      {project.description || 'Support verified carbon offset projects'}
                    </p>
                    <div className="project-details">
                      <div className="detail-item">
                        <span className="detail-label">Location:</span>
                        <span className="detail-value">{project.location || 'Global'}</span>
                      </div>
                      <div className="detail-item">
                        <span className="detail-label">Offset Rate:</span>
                        <span className="detail-value">{co2ePerDollar} kg COâ‚‚e per $1</span>
                      </div>
                    </div>
                    <div className="offset-calculation">
                      <div className="calculation-info">
                        <p>To offset your current footprint:</p>
                        <div className="cost-display">
                          <span className="cost-amount">${estimatedCost}</span>
                          <span className="cost-note">estimated</span>
                        </div>
                      </div>
                    </div>
                  </div>
                </div>
              );
            })
          )}
        </div>
      )}

      <div className="offset-info">
        <h3>About Carbon Offsets</h3>
        <p>
          Carbon offsets allow you to compensate for your emissions by funding projects that reduce 
          or remove COâ‚‚ from the atmosphere. These projects are verified by standards like Gold Standard 
          to ensure they deliver real environmental benefits.
        </p>
      </div>
    </div>
  );
};

export default OffsetSuggestions;
